jQuery(document).ready(function(){ANCORA_GLOBALS.media_frame=null;ANCORA_GLOBALS.media_link=""});function ancora_show_media_manager(a){ANCORA_GLOBALS.media_link=jQuery(a);if(ANCORA_GLOBALS.media_frame){ANCORA_GLOBALS.media_frame.open();return false}ANCORA_GLOBALS.media_frame=wp.media({title:ANCORA_GLOBALS.media_link.data("choose"),library:{type:"image"},multiple:ANCORA_GLOBALS.media_link.data("multiple")===true?"add":false,button:{text:ANCORA_GLOBALS.media_link.data("update"),close:true}});ANCORA_GLOBALS.media_frame.on("select",function(b){var c=jQuery("#"+ANCORA_GLOBALS.media_link.data("linked-field")).eq(0);var e="";if(ANCORA_GLOBALS.media_link.data("multiple")===true){ANCORA_GLOBALS.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=ANCORA_GLOBALS.media_frame.state().get("selection").first().toJSON().url}c.val(e);c.trigger("change")});ANCORA_GLOBALS.media_frame.open();return false};